package com.lumatest.base;import io.github.bonigarcia.wdm.WebDriverManager;import org.openqa.selenium.WebDriver;import org.openqa.selenium.chrome.ChromeDriver;import org.testng.annotations.AfterMethod;import org.testng.annotations.BeforeMethod;public abstract class BaseTest{    private WebDriver driver;        @BeforeMethod    protected void setUpDriver(){                WebDriverManager.chromedriver().setup();        createChromeDriver();    }        private void createChromeDriver(){                if (this.driver == null) {            this.driver = new ChromeDriver();                    }    }        public WebDriver getDriver(){                return driver;    }        @AfterMethod(alwaysRun=true)    protected void tearDown (){            if (this.driver != null) {                        getDriver().quit();            this.driver = null;        }    }}